void ShellSort(int* pNumber)
{
	//int iCount = 0;
	int iGap = SIZE;

	for (iGap /= 2;iGap>0; iGap/=2)
	{
		InsertSort(pNumber,0, iGap);		
	}
	//cout << "Shell : " << iCount << endl;
}

void QuickSort(int* pNumber, int iStart, int iEnd)
{
	if (iStart >= iEnd) return;
	int iPivot = iStart;
	int iLeft = iStart+1;
	int iRight = iEnd;
	
	while (iLeft <= iRight)
	{
		while (pNumber[iLeft] < pNumber[iPivot]) iLeft++;
		while (pNumber[iRight] > pNumber[iPivot]) iRight--;
		if (iLeft < iRight)
		{
			Swap(&pNumber[iLeft], &pNumber[iRight]);
		}
		else
			break;
	}
	Swap(&pNumber[iPivot], &pNumber[iRight]);
	//왼쪽
	QuickSort(pNumber, iStart, iRight - 1);

	//오른쪽
	QuickSort(pNumber, iRight + 1, iEnd);


}

